mapper.update:
update clazz
id = #{id}, name = #{name}, room = #{room}, begin_date = #{beginDate}, end_date = #{endDate}, master_id = #{masterId}, subject = #{subject},
where id=#{id}

mapper.select:
select c.*, e.name as masterName, case when now() > c.end_date then '已结课' when now() < c.begin_date then '未开班' else '在读中' end as status from clazz c left join emp e on c.master_id = e.id
and c.name like concat('%', #{name}, '%')
and c.begin_date >= #{begin}
and c.end_date <= #{end}
order by c.update_time desc

select count(*) from clazz c


        and c.name like concat('%', #{name}, '%')


        and c.begin_date >= #{begin}


        and c.end_date <= #{end}
